package javabase;

public class Task6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Factorial number using for loop
		
		int n=8,fact=1;
		 
        for(int i=1;i<=n;i++)
  	      {

    	   fact=fact*i;
	 	  }

	        System.out.println("8 Factorial is = "+fact);
				

	}

}


//Output
// 8 Factorial is = 120
